// Navbar.jsx
import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './App.css';
import { FaBars, FaTimes } from 'react-icons/fa'; // Voor hamburger en sluiten iconen
import SocialMediaIcons from "./SocialMediaIcons";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-white shadow-md fixed w-full z-50">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          {/* Logo */}
          <div className="flex-shrink-0 flex items-center">
            <Link to="/" className="font-larsseit-medium text-xl">
              YUKON CLUB
            </Link>
          </div>

          {/* Mobile menu button */}
          <div className="flex items-center md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="inline-flex items-center justify-center p-2 rounded-md text-gray-700 hover:text-gray-900 hover:bg-gray-100 focus:outline-none"
            >
              {isOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
            </button>
          </div>

          {/* Desktop menu */}
          <div className="hidden md:flex md:items-center md:space-x-8">
            <Link to="/music" className="nav-link hover:text-gray-900">MUSIC</Link>
            <Link to="/videos" className="nav-link hover:text-gray-900">VIDEOS</Link>
            <Link to="/tour" className="nav-link hover:text-gray-900">TOUR</Link>
            <Link to="/contact" className="nav-link hover:text-gray-900">CONTACT</Link>
          </div>
        </div>

        {/* Mobile menu */}
        <div className={`${isOpen ? 'block' : 'hidden'} md:hidden`}>
          <div className="pt-2 pb-3 space-y-1">
            <Link 
              to="/music" 
              className="block py-2 nav-link hover:bg-gray-100"
              onClick={() => setIsOpen(false)}
            >
              MUSIC
            </Link>
            <Link 
              to="/videos" 
              className="block py-2 nav-link hover:bg-gray-100"
              onClick={() => setIsOpen(false)}
            >
              VIDEOS
            </Link>
            <Link 
              to="/tour" 
              className="block py-2 nav-link hover:bg-gray-100"
              onClick={() => setIsOpen(false)}
            >
              TOUR
            </Link>
            <Link 
              to="/contact" 
              className="block py-2 nav-link hover:bg-gray-100"
              onClick={() => setIsOpen(false)}
            >
              CONTACT
            </Link>
          </div>
        </div>
      </div>
      <div className="social-icons-header">
        <SocialMediaIcons />
      </div>
    </nav>
  );
};

export default Navbar;
