// Navbar.jsx
import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './App.css';
import { FaBars, FaTimes } from 'react-icons/fa'; // Voor hamburger en sluiten iconen
import SocialMediaIcons from "./SocialMediaIcons";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="bg-black text-white p-4 fixed w-full top-0 z-50">
      <div className="container mx-auto">
        <div className="flex justify-between items-center">
          <Link to="/" className="text-xl font-larsseit-medium">
            YUKON CLUB
          </Link>

          {/* Mobile menu button */}
          <button
            onClick={toggleMenu}
            className="md:hidden text-white focus:outline-none"
          >
            {isOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
          </button>

          {/* Desktop menu */}
          <div className="hidden md:flex space-x-6">
            <Link to="/music" className="hover:text-gray-300">MUSIC</Link>
            <Link to="/videos" className="hover:text-gray-300">VIDEOS</Link>
            <Link to="/tour" className="hover:text-gray-300">TOUR</Link>
            <Link to="/contact" className="hover:text-gray-300">CONTACT</Link>
          </div>
        </div>

        {/* Mobile menu */}
        <div className={`${isOpen ? 'block' : 'hidden'} md:hidden mt-4`}>
          <div className="flex flex-col space-y-4">
            <Link to="/music" className="hover:text-gray-300" onClick={toggleMenu}>MUSIC</Link>
            <Link to="/videos" className="hover:text-gray-300" onClick={toggleMenu}>VIDEOS</Link>
            <Link to="/tour" className="hover:text-gray-300" onClick={toggleMenu}>TOUR</Link>
            <Link to="/contact" className="hover:text-gray-300" onClick={toggleMenu}>CONTACT</Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
