import React, { useState } from 'react';
import axios from 'axios';

const Newsletter = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus('sending');

    try {
      const response = await axios.post('https://api.sendgrid.com/v3/marketing/contacts', {
        contacts: [{ email }]
      }, {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_SENDGRID_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.status === 202) {
        setStatus('success');
        setEmail('');
      } else {
        setStatus('error');
      }
    } catch (error) {
      setStatus('error');
      console.error('Error:', error);
    }
  };

  const isValidEmail = (email) => {
    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  return (
    <div className="max-w-md mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-larsseit-medium mb-4 text-center">Newsletter</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-gray-300"
              required
            />
          </div>
          <button
            type="submit"
            disabled={!isValidEmail(email) || status === 'sending'}
            className={`w-full py-2 px-4 rounded-md font-larsseit-medium transition-colors
              ${!isValidEmail(email) || status === 'sending'
                ? 'bg-gray-300 cursor-not-allowed'
                : 'bg-black text-white hover:bg-gray-800'}`}
          >
            {status === 'sending' ? 'Sending...' : 'Subscribe'}
          </button>
        </form>
        {status === 'success' && (
          <p className="mt-4 text-green-600 text-center">Thank you for subscribing!</p>
        )}
        {status === 'error' && (
          <p className="mt-4 text-red-600 text-center">Something went wrong. Please try again.</p>
        )}
      </div>
    </div>
  );
};

export default Newsletter;
